import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [pattern, setPattern] = useState('');
  const [sendValue, setSendValue] = useState('');
  const [responseMessage, setResponseMessage] = useState('');

  useEffect(() => {

    const fetchPattern = async () => {
      try {
        const response = await axios.get('/api/memorycolourmaster/getpattern');
        // Update the state with the response message
        setPattern(response.data.message);
      } catch (error) {
        // Handle error case
        console.error("Error fetching the greeting: ", error);
      }      
    };

   fetchPattern(); 

}, []); // The empty array ensures the effect runs only once (when the component mounts)




  const handleSquareClick = (colour) => {
    console.log(`${colour} square clicked!`);
  };

  const getPattern = async () => {
      try {
        const response = await axios.get('/api/memorycolourmaster/getpattern');
        // Update the state with the response message
        setPattern(response.data.message);
      } catch (error) {
        // Handle error case
        console.error("Error fetching the greeting: ", error);
      }      
   };

   const clearPattern = async () => {
    try {
      const response = await axios.get('/api/memorycolourmaster/clearpattern');
      setResponseMessage(response.data.message);
      setPattern("");
    } catch (error) {
      console.error("Error clearing the pattern : ", error);
    }
  };

  //POST
  // Send message to ASP.NET 
  const sendPattern = async () => {
    try {
      const response = await axios.post('/api/message/send', { text: sendValue});
      setResponseMessage(response.data.message);
      //setInputValue(''); // Clear input field after sending
      setSendValue('');
    } catch (error) {
      console.error("Error sending the message: ", error);
    }
  };





  return (
    <div className="App" style={styles.container}>
      <div style={styles.gameboard}>
        <div
          style={{ ...styles.square, backgroundColor: 'red'}}
          onClick={() => handleSquareClick('Red')}
         ></div>
        <div
          style={{ ...styles.square, backgroundColor: 'blue'}}
          onClick={() => handleSquareClick('Blue')}
        ></div>
        <div
          style={{ ...styles.square, backgroundColor: 'green'}}
          onClick={() => handleSquareClick('Green')}
        ></div>
        <div
          style={{ ...styles.square, backgroundColor: 'yellow'}}
          onClick={() => handleSquareClick('Yellow')}
        ></div>

      </div>

      <div id="player_ui" className='ui' style={styles.ui}>
        <h1>{pattern}</h1>
        <button onClick={getPattern}>Generate new pattern </button>
        <button onClick={clearPattern}>Clear pattern </button>
        <h2>{responseMessage}</h2>
      </div>

    </div>
  );
}

//Inline styles
const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column', // Arrange children in a column
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    gap: '20px',
  },
  gameboard: {
    width: '70%',
    height: '90vh', // Full height of the viewport
    display: 'flex', // Use flexbox for centering
    justifyContent: 'center', // Center horizontally
    alignItems: 'center', // Center vertically
    backgroundColor: '#e0e0e0', // Background color for visibility
    position: 'relative', // To manage absolute positioning if needed
  },
  square: {
    width: '150px',
    height: '150px',
    cursor: 'pointer',
  },
  ui: {
    backgroundColor: '#f0f0f0',
    padding: '20px',
    borderRadius: '10px',
    textAlign: 'center',
    boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
    display: 'flex',
    flexDirection: 'column', // Stack items vertically
    alignItems: 'center', // Center align items horizontally
    gap: '10px', // Add some space between items
  },
};

export default App;
